var delays = [{
  at:2,
  delay: 1000
},
{
  at:16,
  delay: 1000
}];

class Introduction extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      description: ""
    };
  }

  addDesc = () => {
    this.setState({description: "I'm currently a system QA engineer at Shopee Singapore; On the sidelines, I write on technical topics, build/hack random things on my own, and enjoy dwelving into philosophy topics. I consider my life lived when I have engineered a significant solution for mother earth."});
  }

  render(){
    return(
      <div className="intro">s
        <h1 className="intro-text">
          <TypeWriter typing={1} delayMap={delays} onTypingEnd ={ () => this.addDesc()}>
            Hi,<br/> I'm Eva Hiew, <br/>
          </TypeWriter>
          {this.state.description}
        </h1>

        <img src={profilepic} className="intro-img" alt="pic"/>
      </div>
    );
  }
}

function App() {
  return (
    <div className="homepage">
    <Introduction/>
    </div>
  );
}

export default App;
