// import logo from './logo.svg';
import './App.css';
import profilepic from './medium_profile_pic_2.png';
import React from 'react'
import TypeWriter from 'react-typewriter';
import FadeIn from 'react-fade-in';
// import linkedinlogo from './linkedin_logo.png';
import maillogo from './mail_logo.png';
//
// function App() {
//   return (
//     <div className="App">
//       <header className="App-header">
//         <img src={logo} className="App-logo" alt="logo" />
//         <p>
//           Edit <code>src/App.js</code> and save to reload.
//         </p>
//         <a
//           className="App-link"
//           href="https://reactjs.org"
//           target="_blank"
//           rel="noopener noreferrer"
//         >
//           Learn React :)XD
//         </a>
//       </header>
//     </div>
//   );
// }
//
// export default App;


var delays = [{
  at:2,
  delay: 1000
},
{
  at:16,
  delay: 1000
}];

class Introduction extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      description: null
    };
  }

  addDesc = () => {
    this.setState({
      description: [<FadeIn delay={800} transitionDuration={800}>I'm currently a system QA engineer at Shopee Singapore;
      <br/>
      On the sidelines, I write on technical topics, build/hack random things on my own, and enjoy dwelving into philosophy topics;
      <br/>
      I consider my life lived when I have engineered a significant solution for mother earth;</FadeIn>]
    });
  }

  render(){
    return(

        <div className="intro">

          <div className="intro-text">
            <h1>
              <TypeWriter typing={1} delayMap={delays} onTypingEnd ={ () => this.addDesc()}>
                Hi,<br/> I'm Eva Hiew, <br/>
              </TypeWriter>
            </h1>
            <h3>{this.state.description}</h3>
          </div>

          <FadeIn delay={200} transitionDuration={800}>
            <img src={profilepic} className="intro-img" alt="pic"/>
          </FadeIn>

        </div>

    );
  }
}

class LinkPanel extends React.Component{
  render(){
    return(
      <div className="image-link-panel">
        <a href="mailto:EVAH0001@e.ntu.edu.sg">
          <img src={maillogo} className="image-link" alt="linkedin"/>
        </a>
      </div>

    );
  }
}

function App() {
  return (
    <div className="homepage">
    <Introduction/>
    <LinkPanel/>
    </div>
  );
}

export default App;
